// 스케줄 표 컴포넌트
import { useState, useMemo, useEffect } from 'react';

const startDate = new Date('2025-01-01');
const shifts = ['○', '●▣', 'X', 'XX']; // 주간, 야간, 비번, 휴무

function ScheduleTable({ onLogout }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [Anniversaries, SetAnniversaries] = useState([]);
  const [Workers, setWorkers] = useState([]);

  const currentYear = currentDate.getFullYear();
  const currentMonth = currentDate.getMonth(); // 0-11

  const today = new Date();
  const isCurrentMonthView = today.getFullYear() === currentYear && today.getMonth() === currentMonth;
  const todaysDate = isCurrentMonthView ? today.getDate() : null;

  const monthDetails = useMemo(() => {
    const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    const dayLabels = ['일', '월', '화', '수', '목', '금', '토'];

    return Array.from({ length: daysInMonth }, (_, i) => {
      const date = i + 1;
      const dayOfWeek = new Date(currentYear, currentMonth, date).getDay(); // 0: Sun, 6: Sat

      const dailyAnniversaries = Anniversaries.filter((anni) => {
        return parseInt(anni.locdate) === date; // assuming anni.locdate is a string representing the day
      });
      return {
        date,
        dayLabel: dayLabels[dayOfWeek],
        isWeekend: dayOfWeek === 0 || dayOfWeek === 6,
        isToday: date === todaysDate,
        Anniversary: dailyAnniversaries,
      };
    });
  }, [currentYear, currentMonth, todaysDate, Anniversaries]);

  const daysDiff = useMemo(() => {
    const firstDay = new Date(currentYear, currentMonth, 1);
    const timeDiff = firstDay.getTime() - startDate.getTime();
    const Diff = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
    return Diff;
  }, [currentMonth]);

  useEffect(() => {
    async function getAnniData() {
      try {
        const response = await fetch(`/api/anniversary?year=${currentYear}&month=${currentMonth}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const result = await response.json();

        // API 응답 구조에 따라 안전하게 데이터 접근 및 상태 업데이트
        const items = result?.data?.response?.body?.items?.item;

        if (Array.isArray(items)) {
          SetAnniversaries(items);
        } else if (items) {
          // 단일 객체인 경우 배열로 변환
          console.log(items);

          SetAnniversaries([items]);
        } else {
          // 데이터가 없거나 예상치 못한 구조인 경우
          SetAnniversaries([]);
        }
        console.log(items);
      } catch (error) {
        console.error('기념일 데이터를 가져오는 중 오류 발생:', error);
        SetAnniversaries([]); // 오류 발생 시 빈 배열로 설정
      }
    }

    getAnniData();
  }, [currentYear, currentMonth]); // 의존성 추가

  useEffect(() => {
    async function getWorkers() {
      const result = await fetch(`/DB/getWorkers`);
      const data = await result.json();
      console.log(data);
      setWorkers(data);
    }
    getWorkers();
  }, []);

  const getWorkerSchedule = (days, daysDiff) => {
    const schedule = {};
    Workers.forEach((worker) => {
      // 각 근무자의 index를 스케줄 패턴의 시작점으로 사용합니다.
      const startIndex = parseInt(worker.Worker_ID);
      console.log(startIndex);
      schedule[worker.Name] = Array.from({ length: days }, (_, i) => shifts[(daysDiff + i + startIndex) % shifts.length]);
    });
    return schedule;
  };

  const scheduleData = useMemo(() => getWorkerSchedule(monthDetails.length, daysDiff), [currentMonth]);

  const goToPreviousMonth = () => {
    setCurrentDate(new Date(currentYear, currentMonth - 1, 1));
  };

  const goToNextMonth = () => {
    setCurrentDate(new Date(currentYear, currentMonth + 1, 1));
  };

  return (
    <div className="bg-gray-50 min-h-screen font-sans">
      <main className="p-4 sm:p-6 lg:p-8">
        <div className="max-w-7xl mx-auto">
          <div className="flex justify-center items-center mb-6">
            <button onClick={goToPreviousMonth} className="p-2 rounded-full hover:bg-gray-200 transition-colors">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <h1 className="text-xl sm:text-2xl font-bold text-gray-800 w-40 sm:w-48 text-center mx-2">
              {currentYear}년 {currentMonth + 1}월
            </h1>
            <button onClick={goToNextMonth} className="p-2 rounded-full hover:bg-gray-200 transition-colors">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>

          <div className="overflow-x-auto bg-white rounded-lg shadow">
            <table className="min-w-full divide-y divide-gray-200 border-collapse">
              <thead className="bg-gray-100">
                <tr>
                  <th rowSpan="2" className="sticky left-0 bg-gray-300 px-4 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider z-20 align-middle border-b border-gray-200">
                    근무자
                  </th>
                  {monthDetails.map(({ date, isWeekend, isToday, Anniversary }) => (
                    <th key={`date-${date}`} className={`px-4 py-2 text-center text-xs font-bold uppercase tracking-wider transition-colors duration-200 ${isWeekend || Anniversary.length > 0 ? 'bg-red-100 text-red-800' : 'bg-gray-200 text-gray-600'} `}>
                      {/* {Anniversary.length>0?<span className="inline-flex items-center justify-center h-4 w-4 rounded-full bg-red-500 text-white">{date}</span> :date} */}
                      {date}
                    </th>
                  ))}
                </tr>
                <tr>
                  {monthDetails.map(({ date, dayLabel, isWeekend, isToday, Anniversary }) => (
                    <th key={`day-${date}`} className={`px-4 py-2 text-center text-xs font-bold uppercase tracking-wider border-b border-gray-200 transition-colors duration-200 ${isWeekend || Anniversary.length > 0 ? 'bg-red-100 text-red-800' : 'bg-gray-200 text-gray-500'}`}>
                      {dayLabel}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="bg-white ">
                {Workers.map((worker) => (
                  <tr key={worker.Worker_ID}>
                    <td className="sticky left-0 bg-gray-200 px-4 py-3 whitespace-nowrap text-sm font-semibold text-gray-900 z-10 border-r">{worker.Name}</td>
                    {monthDetails.map(({ isWeekend, isToday, Anniversary }, index) => {
                      if (worker.Roll === 'manager') {
                        const shift = isWeekend ? 'XX' : '○';
                        return (
                          <td key={index}>
                            <div className={`w-full h-full flex items-center justify-center py-3 ${isWeekend || Anniversary.length > 0 ? 'bg-red-100' : 'bg-gray-100'}`}>{shift}</div>
                          </td>
                        );
                      } else {
                        return (
                          <td key={index}>
                            <div className={`w-full h-full flex items-center justify-center py-3 ${isWeekend || Anniversary.length > 0 ? 'bg-red-100' : 'bg-gray-100'}`}>{scheduleData[worker.Name][index]}</div>
                          </td>
                        );
                      }
                    })}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="mt-6 flex items-center justify-end space-x-4 text-sm text-gray-600">
            <div className="flex items-center">
              <span className="w-4 h-4 rounded-full bg-blue-100 mr-2 border border-blue-300"></span>
              <span>주간/야간</span>
            </div>
            <div className="flex items-center">
              <span className="w-4 h-4 rounded-full bg-gray-200 mr-2 border border-gray-300"></span>
              <span>휴무</span>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default ScheduleTable;
